mapper.select:
SELECT album_title AS albumTitle, album_contents AS albumContents, created_at AS createdAt
FROM t_album
WHERE ialbum = #{ialbum}

SELECT album_pic AS albumPic
FROM t_album_pics
WHERE ialbum = #{ialbum}

mapper.insert:
INSERT INTO t_album
SET iteacher = #{iteacher}
, album_title = #{albumTitle}
, album_contents = #{albumContents}

