mapper.select:
SELECT F.ifull_notice as iFullNotice, F.full_title as fullTitle , T.teacher_nm as writer,F.full_notice_fix as fullNoticeFix , F.created_at as createdAt FROM t_full_notice F JOIN t_teacher T ON F.iteacher = T.iteacher where F.full_notice_fix = 0
AND full_title like concat ('%', #{search}, '%')
ORDER BY F.created_at DESC limit #{starIdx}, #{row}

SELECT ifull_pic AS ifullPic, full_pic AS pic
FROM t_full_notice_pics
where ifull_notice = #{iFullNotice}

mapper.update:
UPDATE t_full_notice
SET iteacher = #{iteacher},
full_title = #{fullTitle},
full_contents = #{fullContents},
full_notice_fix = #{fullNoticeFix}
WHERE ifull_notice = #{ifullNotice}

mapper.delete:
DELETE
FROM t_full_notice_pics
WHERE ifull_pic in (

    #{ifullPic}
)

mapper.select:
SELECT COUNT(ifull_pic)
FROM t_full_notice_pics
WHERE ifull_notice = #{ifullNotice}

SELECT full_pic, ifull_pic
FROM t_full_notice_pics
WHERE ifull_notice = #{ifullNotice}
