mapper.select:
A.imemory, A.memory_title as memoryTitle, A.memory_contents as memoryContents , A.iteacher, D.teacher_nm as teacherNm, A.created_at as createdAt FROM t_memory A JOIN t_memory_room B ON A.imemory = B.imemory JOIN t_kid C ON B.ikid = C.ikid JOIN t_teacher D ON A.iteacher = D.iteacher
C.iclass = #{iclass}
(A.memory_title like concat ('%', #{search}, '%') OR C.kid_nm like concat ('%', #{search}, '%'))
GROUP BY a.imemory ORDER BY a.imemory desc LIMIT #{startIdx}, #{rowCount}

A.imemory, A.memory_title as memoryTitle, A.memory_contents as memoryContents , A.iteacher, D.teacher_nm as teacherNm, A.created_at as createdAt FROM t_memory A JOIN t_memory_room B ON A.imemory = B.imemory JOIN t_kid C ON B.ikid = C.ikid JOIN t_teacher D ON A.iteacher = D.iteacher
C.ikid = #{ikid}
(A.memory_title like concat ('%', #{search}, '%') OR C.kid_nm like concat ('%', #{search}, '%'))
GROUP BY a.imemory ORDER BY a.imemory desc LIMIT #{startIdx}, #{rowCount}

SELECT ikid, kid_nm as kidNm
FROM t_memory_room
WHERE imemory = #{imemory}

SELECT memory_pic
FROM t_memory_album
WHERE imemory = #{imemory}

SELECT memory_title AS memoryTitle , memory_contents AS memoryContents
FROM t_memory
WHERE imemory = #{imemory}

select memory_pic as memoryPic
from t_memory_album
where imemory = #{imemory}

select imemory
from t_memory
where imemory = #{imemory}

SELECT ikid
FROM t_memory_room
WHERE imemory = #{imemory}

mapper.insert:
INSERT INTO t_memory_room
( imemory, ikid )
VALUES(

    #{imemory}, #{ikid}
)

INSERT INTO t_memory_album
( imemory, memory_pic )
VALUES
(

    #{imemory}, #{pic}
)

insert into t_memory_comment
set imemory = #{imemory}, memory_commnet = #{memoryComment},
iteacher = #{iteacher}, iparent = #{iparent}

mapper.delete:
DELETE
FROM t_memory_comment
WHERE imemory_comment = #{imemoryComment} AND

    iteacher = #{iteacher}


    iparent = #{iparent}

mapper.update:
UPDATE t_memory
SET memory_title = #{memoryTitle}
, memory_contents = #{memoryContents}
WHERE imemory = #{imemory}

mapper.delete:
DELETE FROM t_memory_album
WHERE imemory_pic in

    #{imemoryPic}

mapper.select:
SELECT count(imemory)
FROM t_memory
WHERE imemory = #{imemory}
