SELECT A.imemory, C.ikid, A.memory_title as memoryTitle, A.memory_contents as memoryContents , A.teacher_nm as teacherNm, A.created_at as createdAt FROM t_memory A JOIN t_memory_room B ON A.imemory = B.imemory JOIN t_kid C ON B.ikid = C.ikid
C.iclass = #{iclass}
(A.memory_title like concat ('%', #{search}, '%') OR C.kid_nm like concat ('%', #{search}, '%'))
LIMIT #{startIdx}, #{rowCount}

SELECT Count(A.imemory) FROM t_memory A JOIN t_memory_room B ON A.imemory = B.imemory JOIN t_kid C ON B.ikid = C.ikid
C.iclass = #{iclass}
(A.memory_title like concat ('%', #{search}, '%') OR C.kid_nm like concat ('%', #{search}, '%'))

