mapper.insert:
INSERT INTO t_notice_board
SET iteacher = #{iteacher}
, ikid = #{ikid}
, notice_title = #{noticeTitle}
, notice_contents = #{noticeContents}

mapper.select:
SELECT inotice, notice_title AS noticeTitle, notice_contents AS noticeContents
FROM t_notice_board
WHERE inotice = #{inotice}
AND ikid = #{ikid}

mapper.update:
UPDATE t_notice_board
SET iteacher = #{iteacher}
, ikid = #{ikid}
, notice_title = #{noticeTitle}
, notice_contents = #{noticeContents}
WHERE inotice = #{inotice}

mapper.select:
SELECT notice_pic
FROM t_notice_pics
WHERE inotice = #{inotice}

mapper.delete:
DELETE
FROM t_notice_board
WHERE inotice = #{inotice}
AND iteacher = #{iteacher}

mapper.select:
SELECT A.inotice, A.notice_title AS noticeTitle, A.notice_contents AS noticeContents, B.kid_nm AS kidNm, B.iclass, A.created_at AS createdAt FROM t_notice_board AS A JOIN t_kid AS B ON A.ikid = B.ikid JOIN t_parent_kid AS C ON B.ikid = C.ikid JOIN t_parent AS D ON C.iparent = D.iparent WHERE D.iparent = #{loginedIuser}
AND c.ikid = #{ikid}
AND year(A.created_at) = #{year}
ORDER BY A.created_at DESC LIMIT #{startIdx}, #{rowCount}

SELECT A.inotice, A.notice_title AS noticeTitle, A.notice_contents AS noticeContents,
B.kid_nm AS kidNm, B.iclass, A.created_at AS createdAt
FROM t_notice_board AS A
JOIN t_kid AS B
ON A.ikid = B.ikid

    WHERE B.iclass = #{iclass}


    WHERE B.state = #{iclass}


    AND year(A.created_at) = #{year}

ORDER BY A.created_at DESC
LIMIT #{startIdx}, #{rowCount}

SELECT COUNT(inotice_pic)
FROM t_notice_pics
WHERE inotice = #{inotice}

SELECT iteacher AS writerIuser, teacher_nm AS writerName, ilevel
FROM t_teacher
WHERE iteacher = #{iteacher}

SELECT iparent AS writerIuser, parent_nm AS writerName, ilevel
FROM t_parent
WHERE iparent = #{iparent}

SELECT notice_pic, inotice
FROM t_notice_pics
WHERE inotice = #{inotice}

mapper.delete:
DELETE
FROM t_notice_comment
WHERE inotice_comment = #{inoticeComment} AND

    iteacher = #{iteacher}


    iparent = #{iparent}
