mapper.select:
select ikid, kid_nm as kidNm, iclass, code
from t_kid
where code = #{code}

select iparent
from t_parent_kid
where ikid = #{ikid}

mapper.insert:
insert into t_parent_kid
set ikid = #{ikid}
, iparent = #{iparent}

mapper.select:
SELECT uid, upw, iparent, ilevel
from t_parent
where uid = #{uid} and upw = #{upw}

mapper.update:
update t_parent
set pr_is_del = 1
where iparent = #{iparent}

mapper.select:
select upw
from t_parent
where uid = #{uid}

mapper.insert:
insert into t_parent
set parent_nm = #{parentNm}
, uid = #{uid}
, upw = #{upw}
, ilevel = 1
, phone_nb = #{phoneNb}
, irelation = #{irelation}
, pr_email = #{prEmail}

mapper.select:
select uid, parent_nm as parentNm, phone_nb as phoneNb, pr_email AS prEmail
from t_parent
where iparent = #{iparent}

mapper.update:
update t_parent
set

    parent_nm = #{parentNm}


    ,phone_nb = #{phoneNb}


    ,pr_email = #{prEmail}


    ,upw = #{upw}

where iparent = #{iparent}

UPDATE t_parent
SET firebase_token = #{firebaseToken}
WHERE iparent = #{iparent}
