mapper.select:
SELECT ikid, iclass, kid_nm as kidNm, profile from t_kid
where iclass = #{kidCheck} and state = 0
AND kid_nm like concat ('%', #{search}, '%')
where state = #{kidCheck}
AND kid_nm like concat ('%', #{search}, '%')
LIMIT #{startIdx},#{rowCount}

SELECT COUNT(ikid)
FROM t_kid

where iclass = #{kidCheck}

        AND kid_nm like concat ('%', #{search}, '%')



    where state = #{kidCheck}

        AND kid_nm like concat ('%', #{search}, '%')

mapper.update:
UPDATE t_kid
SET 
iclass = #{kidCheck}


    state = #{kidCheck}

WHERE ikid in (

    #{ikid}
)

mapper.select:
SELECT distinct iparent
FROM t_parent_kid
where ikid in (

    #{ikid}
)
order by iparent

SELECT ikid
FROM t_parent_kid
where iparent = #{iparent}

SELECT state
FROM t_kid
where ikid in (

    #{ikid}
)

mapper.update:
UPDATE t_parent
SET pr_is_del = #{prIsDel},
ilevel = #{prIsDel}
WHERE iparent = #{iparent}

mapper.select:
SELECT A.iparent, A.parent_nm AS parentNm, A.uid, A.phone_nb AS phoneNb FROM t_parent as A JOIN t_parent_kid AS B ON A.iparent = B.iparent JOIN t_kid AS C ON B.ikid = C.ikid where pr_is_del = #{prIsDel}
AND (C.kid_nm like concat ('%', #{search}, '%') OR A.parent_nm like concat ('%', #{search}, '%'))
LIMIT #{startIdx}, #{rowCount}

SELECT COUNT(iparent)
FROM t_parent
WHERE pr_is_del = #{prIsDel}

SELECT DISTINCT A.iparent, A.parent_nm AS parentNm, A.uid, A.phone_nb AS phoneNb FROM t_parent AS A JOIN t_parent_kid AS B ON A.iparent = B.iparent JOIN t_kid AS C ON B.ikid = C.ikid where A.pr_is_del = #{prIsDel} AND C.iclass = #{iclass}
AND (C.kid_nm like concat ('%', #{search}, '%') OR A.parent_nm like concat ('%', #{search}, '%'))
LIMIT #{startIdx}, #{rowCount}

mapper.delete:
DELETE
FROM t_parent_kid
WHERE iparent in (

    #{iparent}
)

mapper.update:
